import React, { useState, useEffect } from 'react';



const SpinnerScreen = () =>{
    const [loading, setLoading] = useState(false);
    useEffect(() => {
      setLoading(true);
      setTimeout(() => {
        setLoading(false);
      }, 2000);
    }, []);


return (
    <div className="container">
      {loading ? (
       <div className="loader-con">
            <div class="loader"></div>
             <div className='loader-txt'>
                <span className="loader-item">Validating your details...</span>
                <span className="loader-item">Creating your account..</span>
                <span className="loader-item">Finishing up...</span>
                <span className="loader-item">Validating your details...</span>
             </div>
       </div>
        
      ) : (
        <div className="main-content">
            <div className="loader-con">
            <div class="loader"></div>
             <div className='loader-txt'>
                <span className="loader-item">Validating your details...</span>
                <span className="loader-item">Creating your account..</span>
                <span className="loader-item">Finishing up...</span>
                <span className="loader-item">Validating your details...</span>
             </div>
       </div>
          <h1>Hello World!</h1>
          <p>
            This is a demo Project to show how to add animated loading with
            React.
          </p>
          <div className="buttons">
            <button className="btn">
              <a href="#">Read Article</a>
            </button>
            <button className="btn get-quote">
              Generate Quote
            </button>
          </div>
          <div className="quote-section">
            <blockquote className="quote">Lorem ipsum dolor sit amet consectetur adipisicing elit. Aspernatur, esse cupiditate similique id porro recusandae. Velit ullam beatae unde magnam reiciendis nostrum autem, cum earum, id eius repudiandae distinctio possimus.</blockquote>-{' '}
            <span className="author">Author</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default SpinnerScreen;
