import React from "react";
import styled from "styled-components";
const SelectContainer = styled.div`
    position: relative;
    margin: 0;
`;


const SelectLabelButton = styled.button`
    padding: 0.3rem 0.5rem;
    min-width: 7rem;
    font-size: 0.9rem;
    font-weight: 500;
    background-color: #fff;
    border: none;
    border-radius: 5px;
    color: #111;
    align-items: center;
    justify-content: space-between;
    border: 1px solid slategrey;
    cursor: pointer;
    box-shadow: 0 1px 4px 0 #ccc;
    transition: 0.3s ease;
    &:hover {
    background-color: #eee;
    }
`;

const DropdownStyle = styled.div`
position: absolute;
top: 0;
left: 0;
max-height: 40vmax;
min-width: 10rem;
padding: 0.4rem;
display: flex;
flex-direction: column;
border-radius: 5px;
background: #fafafa;
border: 1.5px solid slategrey;
transition: max-height 0.2s ease;
overflow: scroll;
${(p) =>
p.isVisible !== true &&
css`
max-height: 40px;
visibility: hidden;
`}
`;

const DropdownItem = styled.div`
font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
display: flex;
align-items: center;
width: 90%;
margin: 0.15rem 0;
padding: 0.3rem 0.5rem;
font-size: 0.9rem;
font-weight: 400;
color: #333;
border-radius: 0.3rem;
cursor: pointer;
${(p) =>
p.active &&
css`
color: #166edc;
font-weight: 500;
`}
&:hover, :focus, :focus:hover {
background-color: #166edc;
color: #fafafa;
outline: none;
}
`;

export default class Step6 extends React.Component{
    constructor(props){
        super(props)
        this.state ={
            currentValue: "",
            isOpen: false
        }
    }

    handleOpen = () =>{
        this.setState({
            isOpen: true
        })
    }

    hanldeClose = () =>{
        this.setState({
            isOpen: false
        })
    }

    handleChangeValueChange = (value) =>{
        this.setState({
            currentValue: value
        })
    }

    handleChange = (value) =>{
        this.handleChangeValueChangea(value)
        if(onchange){
           onchange(value)
        }
       this.hanldeClose()
    }

    render(){
        let countries = ["USA", "Britain", "Germany", "India", "UAE", "Australia", "Switzerland"]
        return(
        
            <>
               <h1 className="header-title">Addition Information</h1>
               <div className="additional-information">
                    <label className="label">AR/OR Tracking Number</label>
                    <input type="text" className="custom-input2" />
                    <label className="label">Security Question</label>
                    <Select values={countries} onchange={(v)=>console.log(v)}></Select>
                    <SelectContainer>
                        <SelectLabelButton onClick={this.handleOpen}>
                            {this.state.currentValue !== "" ? this.state.currentValue : label}
                        </SelectLabelButton>
                        <DropdownStyle isVisible={open}>
                        {values.map((value, index) => (
                            <DropdownItem
                                onClick={() => handleChange(value)}
                                active={value === currentValue}
                                key={index}>
                            {value}
                            </DropdownItem>
                        ))}
                        </DropdownStyle>
                    </SelectContainer>
               </div>
            </>
        )
    }
}
