import React from "react";
import Select from 'react-select'


const options = [
    { value: 'sq1', label: 'Security Question 1'},
    { value: 'sq2', label: 'Security Question 2' },
    { value: 'sq3', label: 'Security Question 3' }
  ]

  const selectStyles ={
    control: (styles, {isFocused}) => {
        console.log("control", isFocused)
        if(!isFocused){
           return{
            ...styles,
                backgroundColor: "#F4F4F4",
                border: "none",
                height: "56px",
                borderRadius: "10px",
                color: "black",
                fontWeight: 500,
                fontSize:"20px"
           }
        }
        else{
            return{
                ...styles,
                    backgroundColor: "#F4F4F4",
                    backgroundColor: "#F4F4F4",
                    border: "1px solid transparent",
                    height: "56px",
                    borderRadius: "10px",
                    color: "black",
                    fontWeight: 500,
                    fontSize:"20px"
               } 
        }
           
        
    },
        
    
    option: (styles, {data, isFocused, isSelected, isDisabled}) =>{
        if(isFocused){
            return{...styles, 
                color: "pink",
                backgroundColor: "red"
           }
        }
    },
    menu: (styles) =>({
        ...styles,
        backgroundColor: "#F4F4F4",
        padding: "13px 14px"
    })
  }

export default class Step6 extends React.Component{
    constructor(props){
        super(props)
        this.state ={
            currentValue: "",
            isOpen: false
        }
    }

    handleChange =(selectedOption) =>{
       console.log("handleChange", selectedOption)
    }

    render(){
    
        return(
            <>
               <h1 className="header-title">Addition Information</h1>
               <div className="additional-information">
                    <label className="label">AR/OR Tracking Number</label>
                    <input type="text" className="custom-input2" />
                    <label className="label">Security Question</label>
                    <Select options={options} onChange={this.handleChange} styles={selectStyles} label="-- Security Question --"/>
               </div>
            </>
        )
    }
}
