import React from "react";
import retailAcc from "../../assets/images/icon/retailAcc.png"
import dealerAcc from "../../assets/images/icon/dealerAcc.png";


class RegistrationStep1 extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            isActiveRetailer: true,
            isActiveDelear: false
        }
        this.handleRetailAccount =this.handleRetailAccount.bind(this)
        this.handleDealerAccount =this.handleDealerAccount.bind(this)
    }
    
    handleRetailAccount(e){
      e.preventDefault();
      this.setState({
        isActiveRetailer: !this.isActiveRetailer,
      })
      alert('retailer')
    }

    handleDealerAccount(e){
        e.preventDefault();
        this.setState({
          isActiveDelear: !this.isActiveDelear,
        })
        alert('retailer')
      }
    render(){
        var classActive1 = this.state.isActiveRetailer ? "card-icon-active" : null;
        var classActive2 = this.state.isActiveDelear ? "card-icon-active" : null;
        return(
            <>
                <h1 className="header-title">Select your preferred</h1>
                <h1 className="header-title-yellow">account type</h1>
                <div className="account-type-container">
                    <div className={`card-icon ${classActive1}`}  onClick={this.handleRetailAccount} onChange={this.handleRetailAccount}>
                        {/* card-header */}
                        <span className="card-icon-header">
                            <span>Retailer Account</span>
                            <div className="card-icon-img"><img class="card-image" src={retailAcc} alt="" srcset="" /></div>
                        </span>
                        {/* card-content */}
                        <div className="card-icon-content">
                            <p>Lorem ipsum dolor sit amet, lorem et al consectetur adipiscing elit:</p>
                            <ul>
                                <li>Package inclusions 1</li>
                                <li>Package inclusions 2</li>
                                <li>Package inclusions 3</li>
                                <li>Package inclusions 4</li>
                                <li>Package inclusions 5</li>
                                <li>Package inclusions 6</li>
                                <li>Package inclusions 7</li>
                            </ul>
                            <p>Lorem ipsum dolor sit amet, lorem et al consectetur adipiscing elit. Lorem ipsum dolor sit amet, lorem et al consectetur adipiscing elit.</p>
                        </div>
                    </div>
                    <div className={`card-icon ${classActive2}`}  onClick={this.handleDealerAccount} onChange={this.handleDealerAccount}>
                        {/* card-header */}
                        <span className="card-icon-header">
                            <span>Dealer Account</span>
                            <div className="card-icon-img"><img class="card-image" src={dealerAcc} alt="" srcset="" /></div>
                        </span>
                        {/* card-content */}
                        <div className="card-icon-content">
                            <p>Lorem ipsum dolor sit amet, lorem et al consectetur adipiscing elit:</p>
                            <ul>
                                <li>Package inclusions 1</li>
                                <li>Package inclusions 2</li>
                                <li>Package inclusions 3</li>
                                <li>Package inclusions 4</li>
                                <li>Package inclusions 5</li>
                                <li>Package inclusions 6</li>
                                <li>Package inclusions 7</li>
                            </ul>
                            <p>Lorem ipsum dolor sit amet, lorem et al consectetur adipiscing elit. Lorem ipsum dolor sit amet, lorem et al consectetur adipiscing elit.</p>
                        </div>
                    </div>
                </div>
                <button className="btn-create">
                    Create Account
                </button>
            </>
        )
    }
}

export default RegistrationStep1;
