import React, { useState, useEffect } from 'react';



const SpinnerScreen = () =>{
    const [loading, setLoading] = useState(false);
    useEffect(() => {
      setLoading(true);
      setTimeout(() => {
        setLoading(false);
      }, 2000);
    }, []);


return (
    <div className="container">
      {loading ? (
       
        <div class="abs-c">
            <div id="text1" class="text-floater">We're loading your reports</div>
            <div id="text2" class="text-floater">Almost Ready...</div>
            <div id="text3" class="text-floater">Done! <span>&#x2714;</span></div>
        
            <div class="loader-rounded">
                <div class="lr-body1"></div>
                <div class="lr-body2"></div>
                <div class="lr-box"></div>
                <div class="lr-tip"></div>
            </div>
        </div>
      ) : (
        <div className="main-content">
          <h1>Hello World!</h1>
          <p>
            This is a demo Project to show how to add animated loading with
            React.
          </p>
          <div className="buttons">
            <button className="btn">
              <a href="#">Read Article</a>
            </button>
            <button className="btn get-quote">
              Generate Quote
            </button>
          </div>
          <div className="quote-section">
            <blockquote className="quote">Lorem ipsum dolor sit amet consectetur adipisicing elit. Aspernatur, esse cupiditate similique id porro recusandae. Velit ullam beatae unde magnam reiciendis nostrum autem, cum earum, id eius repudiandae distinctio possimus.</blockquote>-{' '}
            <span className="author">Author</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default SpinnerScreen;
