import React from "react";
import styled from "styled-components";

const Wrapper = styled.div`
  display: flex;
  height: auto;
  width: 100%;
  padding: 0px 16px 24px 16px;
  box-sizing: border-box;
`;

const Item = styled.div`
  display: flex;
  align-items: center;
  height: 48px;
  position: relative;
`;

const RadioButtonLabel = styled.label`
  position: absolute;
  top: 25%;
  left: 4px;
  width: 24px;
  height: 24px;
  border-radius: 50%;
  background: white;
  border: 1px solid #bebebe;
`;
const RadioButton = styled.input`
  opacity: 0;
  z-index: 1;
  border-radius: 50%;
  width: 80%;
  height: 80%;
  margin-right: 10px;
 
  ${(props) =>
    props.checked &&
    ` 
    &:checked + ${RadioButtonLabel} {
      border: 1px solid #FFC914;
      &::after {
        content: "";
        display: block;
        border-radius: 50%;
        width: 80%;
        height: 80%;
        margin: auto;
        margin-top: 6.2%;
        background-color: #FFC914;
      }
    }
  `}
`;

class RegistrationStep6 extends React.Component{
    constructor(props){
        super(props)
        this.state={
            placement: ""
        }
        this.placementChange = this.placementChange.bind(this)
    }

    placementChange(e){
        console.log(e.target.value)
        this.setState({
            placement: e.target.value
        })
    }

    render(){
        return(
            <>
                <h1 className="header-title">Input your <span className="header-title-yellow">network</span></h1>
                 <h1 className="header-title" >details</h1>
                 <div className="network-details">
                    <label className="label">Direct Referral RC</label>
                    <input type="text" className="custom-input2" />
                    <label className="label">Placement</label>
                    <input type="text" className="custom-input2" />
                    <Wrapper>
                        <Item>
                            <RadioButton
                            type="radio"
                            name="radio"
                            value="left"
                            checked={this.state.placement === "left"}
                            onChange={this.placementChange}
                            />
                            <RadioButtonLabel />
                            <div>Left</div>
                        </Item>
                        <Item>
                            <RadioButton
                            type="radio"
                            name="radio"
                            value="right"
                            checked={this.state.placement === "right"}
                            onChange={this.placementChange}
                            />
                            <RadioButtonLabel />
                            <div>Right</div>
                        </Item>
                    </Wrapper>
                 </div>
            </>
        )
    }
}

export default RegistrationStep6;
