import React from "react";
import "../../../styles/register.css";
import arrowBack from "../../../assets/images/icon/arrowBack.png"
import Step1 from "./Step1";
import Step2 from "./Step2";
import Step3 from "./Step3";
import Step4 from "./Step4";
// import SpinnerScreen from "./SpinnerScreen";



export default class RegistrationRetailer extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            step: 1
        }
    }

    prevStep = () =>{
       this.setState({
        step: this.state.step - 1
       })
    }

    nextStep = () =>{
        this.setState({ step: this.state.step + 1 });
    }

    progressHeader(){
        return(
            <>
            <button className="btn-back" onClick={this.prevStep}><img src={arrowBack} alt="" srcset="" /></button>
                <div className="progress-bar"><div className="progress-bar-inner"></div></div>
                <span className="pager">
                    <span className="pager-count">{this.state.step} </span>
                    <span className="pager-total">| 4</span>
                </span>
            </>
            
        )
    }

    progressHeader2(){
        return(
            <>
            
                <div className="progress-bar"><div className="progress-bar-inner"></div></div>
                <span className="pager">
                    <span className="pager-count">{this.state.step} </span>
                    <span className="pager-total">| 4</span>
                </span>
            </>
            
        )
    }

    
    render(){
        
        const steps = this.state.step;
        let StepCom;
        let back;

        if(steps===1){
           StepCom = <Step1 nextStep={this.nextStep}/>
           back =  <this.progressHeader2/>
        }
        else if(steps===2){
            StepCom = <Step2 prevStep={this.prevStep} nextStep={this.nextStep}/>
            back =  <this.progressHeader/>
        }
        else if(steps===3){
            StepCom = <Step3 prevStep={this.prevStep} nextStep={this.nextStep}/>
            back =  <this.progressHeader/>
        }
        else if(steps===4){
            StepCom = <Step4 prevStep={this.prevStep} nextStep={this.nextStep}/>
            back =  <this.progressHeader/>
        }
        return(
          <div className="resgister-step">
                {/* Header Progress */}
                <div className="progress-step-con">
                    
                        
                </div>
                {/* Content */}
                <div className="register-step-content"> 
                    {StepCom}
                </div>
          </div>
        )
    }
}
